import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Redux, { createStore } from 'redux'
import App from './App';


// 函数式计数器
class Counter extends Component {
  render() {
    return (
      <div>
        {/* <h1>计数数量：{props.value}</h1> */}
        <button onClick={() => this.numChange(1)}>计数+1</button>
        <button onClick={() => this.numChange(-1)}>计数-1</button>
      </div>
    )
  }

  numChange = (data) => {
    //  通过仓库的方法dispatch 进行数据修改
    // console.log(data);
    store.dispatch({ type: (data > 0 ? 'add' : 'sub') });
  }
}

const store = createStore(operation);

function operation(state = { num: 0 }, action){
  switch (action.type) {
    case 'add':
      state.num++;
      break;
    case 'sub':
      state.num--;
      break;
  }
  return {...state};
};

ReactDOM.render(
  <Counter />,
  document.getElementById('root')
);
