  render() {
    return (
      <div>
        {/* <h1>计数数量：{props.value}</h1> */}
        <button onClick={this.add}>计数+1</button>
        <button onClick={this.decrement}>计数-1</button>
      </div>
    )
  }
  add = (data) => {
    //  通过仓库的方法dispatch 进行数据修改
    console.log(data);
    console.log('add');
  }
  decrement = () => {
    //  通过仓库的方法dispatch 进行数据修改
  }
}


ReactDOM.render(
  <Counter />,
  document.getElementById('root')
);
