import React, { Component, Profiler } from 'react';
import ReactDOM from 'react-dom';
import Redux, { createStore } from 'redux';
import { Povider, connect } from 'react-redux';

// 函数式计数器
class Counter extends Component {
  render() {
    console.log('====================================');
    console.log(this.props);
    console.log('====================================');
    return (
      <div>
        {/* 3. 获取数据 */}
        <h1>计数数量：{this.props.value}</h1>
        <button onClick={this.props.numChange(1)}>计数+1</button>
        <button onClick={this.props.numChange(-1)}>计数-1</button>
      </div>
    )
  }
}

//1. 创建数据仓库
const store = createStore(reducer);

//2.  数据处理函数
// reduce 2个作用：一，初始化数据；二，通过获取动作改变数据。
function reducer(state = { num: 0 }, action){
  switch (action.type) {
    case 'add':
      state.num++;
      break;
    case 'sub':
      state.num--;
      break;
    default:
      break;
  }
  // return {...state}; // 旧版本需要对数据进行解构
  return state;
};

// 创建一个动作
const addAction = {
  type: 'add'
}

// 将state 映射到props 函数
function mapStateToProps(state) {
  return {
    value: state.num
  }
}
// 将修改state数据的方法 映射到props, 默认传入store里的dispatch方法。
function mapDispatchToProps(dispatch) {
  return {
    numChange: ()=>{dispatch(addAction)}
  }
}

// 将上面的2个方法，映射到新组建上

const NewAPP = connect(
  mapStateToProps,
  mapDispatchToProps
)(Counter);


ReactDOM.render(
  <Profiler store="store">
    <NewAPP></NewAPP>
  </Profiler>,
  document.getElementById('root')
);
