import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';

// 函数式计数器
function Counter(props) {
  return (
    <div>
      <h1>计数数量：{props.value}</h1>
      <button onClick={add(1)}>计数+1</button>
      <button onClick={decrement}>计数-1</button>
    </div>
  )
}

const add = (data) => {
  //  通过仓库的方法dispatch 进行数据修改
  console.log(data);
  console.log('add');
}
const decrement = () => {
  //  通过仓库的方法dispatch 进行数据修改
}

ReactDOM.render(
  <Counter />,
  document.getElementById('root')
);
